import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './home.css';
import Select, { components } from 'react-select';

const Menu = (props) => {
  const { options, children, maxHeight, getValue } = props;
  const selectValue = getValue();

  return (
    <components.Menu {...props}>
      {React.Children.toArray(children).slice(0, 1)} {/* Show only the first 3 options */}
      {selectValue.length < 3 && (
        <div style={{ padding: '8px', textAlign: 'center' }}>
          <em>{options.length - 3} more options...</em>
        </div>
      )}
    </components.Menu>
  );
};




function Home() {
  let history = useNavigate();
  const [listOfPatients, setListOfPatients] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:3001/patient").then((response) => {
      setListOfPatients(response.data);
    });
  }, []);

  const options = listOfPatients.map((patient) => ({
    value: patient.id,
    label: patient.name,
  }));

  const handleSelectChange = (selectedOption) => {
    if (selectedOption) {
      history(`/patient/${selectedOption.value}`);
    }
  };

  return (
    <div>
      <div className="block">
        <div className="block_item">
        <Select
            options={options}
            onChange={handleSelectChange}
            components={{ Menu }} // Use the custom menu
            menuPortalTarget={document.body} // Ensures the menu appears outside the container
          />
        </div>
        <a href="/createCustomer" className="myButton">
          ลูกค้าใหม่
        </a>
      </div>
    </div>
  );
}

export default Home;
