import React, {useEffect, useState} from 'react'
import { useParams } from 'react-router-dom'
import axios from 'axios';
import './patient.css';
import {Formik, Form, Field, ErrorMessage, validateYupSchema} from 'formik'
import * as Yup from 'yup';

function Patient() {
    let {id} = useParams();
    const [patient, setPatient] = useState({});
    const [clinic, setClinic] = useState({});
    const [course, setCourse] = useState([]);

    
    useEffect(() => {
      axios.get(`http://localhost:3001/patient/byId/${id}`).then((response) => {
        setPatient(response.data);
      axios.get(`http://localhost:3001/patient/clinic/byId/${response.data.department_id}`).then((clinicResponse) => {
        setClinic(clinicResponse.data);
        console.log(clinicResponse.data)
      axios.get(`http://localhost:3001/course/${id}`).then((courseResponse) =>{
        setCourse(courseResponse.data)
      })
      });
      });
    }, []);

    const onSubmit = (data)=>{
      axios.post('http://localhost:3001/course', data).then((response) => {console.log("Successful sent");
    ;})
    }
  

    const initialValuess = { department_id: `${patient.name}` ,course_name: "", course_detail: "" ,course_price:"",course_times:"",course_comid:"", patientId:""};
    const validationSchema = Yup.object().shape({
      course_name:Yup.string().required(),
      course_detail: Yup.string().required("กรุณาใส่อายุ"),
      course_price: Yup.number().required("กรุณาใส่ราคา"),
      course_times: Yup.number().required("กรุณาจำนวนครั้ง"),
      course_comid: Yup.number().required("กรุณาชื่อผู้แนะนำ"),
      department_id: Yup.number().required("กรุณาชื่อผู้แนะนำ"),
      patientId: Yup.number().required("กรุณาชื่อผู้แนะนำ"),
    })

    
  return (
    <div className='blocks'>
      <div className='blocks_info'>
        <div className='a'>
          <div>ชื่อ: {patient.name}</div>
          <div>อายุ: {patient.age}</div>
          <div>สาขา: {clinic.department} </div>
        </div>
      </div>
      <Formik initialValues={initialValuess} onSubmit={onSubmit} validationSchema={validationSchema}>
          <Form className='block_info'>
            <label>ชื่อคอสต์</label>
            <ErrorMessage name="course_name" component="span" />
            <Field id="input" name="course_name" placeholder="ชื่อคอสต์" />
            <label>ข้อมูลเพิ่มเติม</label>
            <ErrorMessage name="course_detail" component="span" />
            <Field id="input" name="course_detail" placeholder="ข้อมูลเพิ่มเติม" />
            <label>ราคา</label>
            <ErrorMessage name="course_price" component="span" />
            <Field id="input" name="course_price" placeholder="ราคา" />
            <label>จำนวนครั้ง</label>
            <ErrorMessage name="course_times" component="span" />
            <Field id="input" name="course_times" placeholder="จำนวนครั้ง" />
            <label>ผู้แนะนำ</label>
            <ErrorMessage name="course_comid" component="span" />
            <Field id="input" name="course_comid" placeholder="ผู้แนะนำ" />
            <label>สาขา</label>
            <ErrorMessage name="department_id" component="span" />
            <Field id="input" name="department_id" placeholder="สาขา" />
            <label>ลูกค้า</label>
            <ErrorMessage name="patientId" component="span" />
            <Field id="input" name="patientId" placeholder="ลูกค้า" />
          
          {/*<label>แผนก</label>
          <ErrorMessage name="department_id" component="span" />
          <Field as="select" id="input" name="department_id" placeholder="แผนก">
          <option defaultValue >สาขา-แผนก</option>
            {list_of_clinics.map((value, key) => (
              <option  key={key} className="name" value={value.id}>{value.department}</option>
            ))}
          </Field>*/}
          <button type="submit">เพิ่มคอสต์</button>
        </Form>
      </Formik>
    </div>
  )
}

export default Patient
