import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './home.css';
import Select from 'react-select';

function Home() {
  let history = useNavigate();
  const [listOfPatients, setListOfPatients] = useState([]);
  const [selectedPatient, setSelectedPatient] = useState(null);

  useEffect(() => {
    axios.get("http://localhost:3001/patient").then((response) => {
      setListOfPatients(response.data);
    });
  }, []);

  const handlePatientSelect = (selectedOption) => {
    setSelectedPatient(selectedOption);
    if (selectedOption) {
      history(`/patient/${selectedOption.id}`);
    }
  };

  // Transform the patient data to the format required by react-select
  const patientOptions = listOfPatients.map((patient) => ({
    value: patient.id,
    label: `${patient.name} (อายุ ${patient.age})`,
    id: patient.id,
  }));

  return (
    <div>
      <div className="block">
        <div className='block_item'>
          <Select
            options={patientOptions}
            value={selectedPatient}
            onChange={handlePatientSelect}
            placeholder="ค้นหาผู้ป่วย..."
          />
        </div>
        <a href="/createCustomer" className="myButton">ลูกค้าใหม่</a>
      </div>
    </div>
  );
}

export default Home;
