import React, {useEffect, useState} from 'react'
import { useParams } from 'react-router-dom'
import axios from 'axios';
import './patient.css';
import {Formik, Form, Field, ErrorMessage, validateYupSchema} from 'formik'
import * as Yup from 'yup';

import CreateCourse from '../component/createCourse'
import Ppopup from '../component/popup'
import Popup from 'reactjs-popup';

function Patient() {
    let {id} = useParams();
    const [patient, setPatient] = useState({});
    const [clinic, setClinic] = useState({});
    const [course, setCourse] = useState([]);

    
    useEffect(() => {
      axios.get(`http://localhost:3001/patient/byId/${id}`).then((response) => {
        setPatient(response.data);
      axios.get(`http://localhost:3001/patient/clinic/byId/${response.data.department_id}`).then((clinicResponse) => {
        setClinic(clinicResponse.data);
      axios.get(`http://localhost:3001/course/${id}`).then((courseResponse) =>{
        setCourse(courseResponse.data)
        console.log(courseResponse.data)
      
      })
      });
      });
    }, []);

    
  return (
    <div className='blocks'>
      <div className='blocks_info'>
        <div className='a'>
          <div>ชื่อ: {patient.name}</div>
          <div>อายุ: {patient.age}</div>
          <div>สาขา: {clinic.department} </div>
        </div>
      </div>
      <div>
            {course.map((value, key) => (
              <div className="course">
                <div className="course_item" key={key} >ชื่อคอส: {value.course_name}</div>
                <div className="course_item" key={key} >รายละเอียด: {value.course_detail}</div>
                <div className="course_item" key={key} >ราคารวม: {value.course_price}</div>
                <div className="course_item" key={key} >จำนวนครั้ง: {value.course_times}</div>
              </div>
            ))}

      </div>
      <Ppopup componentToRender={<CreateCourse/>}/>
    </div>
  )
}

export default Patient
