import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './home.css';
import Select from 'react-select'


function Home() {

  let history = useNavigate();
  const [listOfPatients, setListOfPatients] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(()=>{
    axios.get("http://localhost:3001/patient").then((response)=>{
      setListOfPatients(response.data);
    })
  },[])

  const options = listOfPatients.map((patient) => ({
    value: patient.id,
    label: patient.name, 
  }));

  const filteredOptions = options.filter(
    (option) => option.label.toLowerCase().indexOf(searchQuery.toLowerCase()) >= 0
  );

  const handleInputChange = (newValue) => {
    const inputValue = newValue.replace(/\W/g, '');
    setSearchQuery(inputValue);
  };

  return (
    <div>
      <Select
        options={filteredOptions}
        onInputChange={handleInputChange}
        inputValue={searchQuery}
      />
      <div className="block">
        <div className='block_item'>
          {listOfPatients.map((value, key) => (
            <div className="block_item_list" key={key} onClick={()=>{history(`/patient/${value.id}`)}}>
              <div className="name">ชื่อ: {value.name}</div>
              <div className="age">อายุ{value.age}</div>
            </div>
          ))}
        </div>
        <a href="/createCustomer" className="myButton">ลูกค้าใหม่</a>
      </div>
    </div>
  );
          }
export default Home;
