import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Select, { components } from 'react-select';
import './home.css';

function Home() {
  let history = useNavigate();
  const [listOfPatients, setListOfPatients] = useState([]);
  const [showOptions, setShowOptions] = useState(false);

  useEffect(() => {
    axios.get("http://localhost:3001/patient").then((response) => {
      setListOfPatients(response.data);
    });
  }, []);

  const options = listOfPatients.map((patient) => ({
    value: patient.id,
    label: patient.name,
  }));

  const handleSelectChange = (selectedOption) => {
    if (selectedOption) {
      history(`/patient/${selectedOption.value}`);
    }
  };

  const handleMenuOpen = () => {
    setShowOptions(true);
  };

  const handleMenuClose = () => {
    setShowOptions(false);
  };

  return (
    <div>
      <div className="block">
        <div className="block_item">
          <Select
            options={options}
            onChange={handleSelectChange}
            menuIsOpen={showOptions}
            onMenuOpen={handleMenuOpen}
            onMenuClose={handleMenuClose}
            menuPortalTarget={document.body} // Ensures the menu appears outside the container
          />
          {listOfPatients.map((value, key) => (
            <div
              className="block_item_list"
              key={key}
              onClick={() => {
                history(`/patient/${value.id}`);
              }}
            >
              <div className="name">ชื่อ: {value.name}</div>
              <div className="age">อายุ{value.age}</div>
            </div>
          ))}
        </div>
        <a href="/createCustomer" className="myButton">
          ลูกค้าใหม่
        </a>
      </div>
    </div>
  );
}

export default Home;
