import './App.css';
import { BrowserRouter as Router, Route, Routes, Link, useNavigate} from 'react-router-dom'

import CCustomer from "./pages/createCustomer";
import Home from "./pages/home";
import Patient from "./pages/patient";
import Login from './pages/login'
import Register from './pages/register'
import axios from 'axios'
import { AuthContext } from './helpers/AuthContex'
import { useState, useEffect } from 'react'
import Cookies from 'js-cookie';
import PrivateRoutes from './utils/PrivateRoute';

function App() {
  const [authState, setAuthState] = useState(false);

  useEffect(()=>{
    axios.get('http://localhost:3001/auth/validate', { headers:{
      accessToken: Cookies.get('accessToken'),
    },
  }).then((response)=>{
      if(response.data.error)
      {
         setAuthState(false);
      }
      else
      {
        setAuthState(true)
      }
    })
  },[])

  return (
    <div className='App'>
      <AuthContext.Provider value={{ authState, setAuthState }}>
        <Router>
          <div className="nav">
          <Link to="/">Home </Link>
          {!authState && (
            <>
              <Link to="/login">login </Link>
              <Link to="/register">register </Link>
            </>
          )}
          </div>
          <Routes>
            <Route element={<PrivateRoutes token={authState}/>}>
              <Route path="/" element={<Home />} />
              <Route path="/createCustomer" element={<CCustomer />} exact />
              <Route path="/patient/:id"element={<Patient />}exact/>
            </Route>
            <Route path="/login" element={<Login />} exact />
            <Route path="/register" element={<Register />} exact />
          </Routes>
        </Router>
      </AuthContext.Provider>
    </div>
  )
}

export default App;
