import React, { useState } from 'react';
import Popup from 'reactjs-popup';
import { AppointmentForm } from '@devexpress/dx-react-scheduler-material-ui';


const Booking = ({data,onSave}) => {
  const [open, setOpen] = useState(false);
  const closeModal = () => setOpen(false);


  const handleSave = (changedAppointment) => {
    onSave(changedAppointment);
  };
  
  return (
    <div>
      <button type="button" className="button" onClick={() => setOpen(o => !o)}>
        Booking
      </button>
      <Popup open={open} closeOnDocumentClick onClose={closeModal}>
          <AppointmentForm
            readOnly={false} // Allow editing in the form
            visible={data !== null} // Show the form only when data is available (editing mode)
            onVisibilityChange={() => handleSave(null)} // Hide the form when it's closed
            {...data}
        />
      </Popup>
    </div>
  );
};

export default Booking
